import React, { useState } from 'react';
import './Recipe.css';
import data from '../Recipe/CocktailData.json';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faMagnifyingGlass,
  faMartiniGlassCitrus,
} from '@fortawesome/free-solid-svg-icons';

const Recipe = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [filter, setFilter] = useState('all'); // 'all', 'top10', 'recommend'

  const recipes = data;

  const openModal = (recipe) => {
    setSelectedRecipe(recipe);
    setModalOpen(true);
  };

  const closeModal = () => {
    setSelectedRecipe(null);
    setModalOpen(false);
  };

  const filterRecipes = () => {
    switch (filter) {
      case 'top10':
        return recipes.slice(0, 10);
      case 'recommend':
      // return recipes.filter((recipe) => );
      default:
        return recipes;
    }
  };

  const [searchTerm, setSearchTerm] = useState('');

  return (
    <>
      <p className='recipe_logo'>
        Cocktail Recipe
        <FontAwesomeIcon
          icon={faMartiniGlassCitrus}
          className='cocktail__logo'
        />
      </p>
      <div className='container'>
        <ul className='local_search'>
          <li>칵테일 검색</li>
        </ul>
        <div className='search_bar'>
          <input
            type='text'
            placeholder='Search...'
            onChange={(event) => {
              setSearchTerm(event.target.value);
            }}
          />
          <FontAwesomeIcon icon={faMagnifyingGlass} className='button' />
        </div>
      </div>
      <div className='category'>
        <button className='category__button' onClick={() => setFilter('all')}>
          전체보기
        </button>
        <button className='category__button' onClick={() => setFilter('top10')}>
          Top 10
        </button>
        <button
          className='category__button'
          onClick={() => setFilter('recommend')}
        >
          혼밤이 추천
        </button>
      </div>
      <div className='kategoria'>
        {filterRecipes().map((recipe) => (
          <div key={recipe.cid} className='recipe_card'>
            {/* 칵테일 레시피 아이디 */}
            <img
              src={recipe.imageURL}
              alt={recipe.cname}
              className='recipe_image'
              onClick={() => openModal(recipe)}
            />
            {/* 이미지 */}
            <div className='recipe_details'>
              <h2 className='name' onClick={() => openModal(recipe)}>
                {recipe.cname}
              </h2>
              {/* 이름 */}
            </div>
          </div>
        ))}
      </div>

      {modalOpen && selectedRecipe && (
        <div className='modal'>
          <div className='modal_content'>
            <span className='close' onClick={closeModal}>
              &times;
            </span>
            <img src={selectedRecipe.imageURL} className='modal_image' />
            {/* 이미지 */}
            <div className='modal_details'>
              <h2>{selectedRecipe.cname}</h2>
              <br />
              {/* 이름 */}
              <p>{`알콜 도수: ${selectedRecipe.degree}`}</p>
              <br />
              {/* 알콜 도수 */}
              <p>{`제조법: ${selectedRecipe.make}`}</p>
              <br />
              {/* 제조법 */}
              <p>{`글라스: ${selectedRecipe.glass}`}</p>
              <br />
              {/* 글라스 */}
              <p>{`주재료: ${selectedRecipe.mainingredient}`}</p>
              <br />
              {/* 주재료 */}
              <p>{`부재료: ${selectedRecipe.submaterials}`}</p>
              <br />
              {/* 부재료 */}
              <p>{`유래: ${selectedRecipe.provenance}`}</p>
              <br />
              {/* 유래 */}
              <p>{`만드는법: ${selectedRecipe.recipe}`}</p>
              <br />
              {/* 만드는법 */}
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Recipe;
