import React, { useState } from 'react';
import './Recipe.css';
import data from '../Recipe/CocktailData.json';

const Recipe = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [filter, setFilter] = useState('all'); // 'all', 'top10', 'recommend'

  const recipes = data;

  const openModal = (recipe) => {
    setSelectedRecipe(recipe);
    setModalOpen(true);
  };

  const closeModal = () => {
    setSelectedRecipe(null);
    setModalOpen(false);
  };

  const filterRecipes = () => {
    switch (filter) {
      case 'top10':
        return recipes.slice(0, 10);
      case 'recommend':
      // 여기에 혼밤이 추천해주는 칵테일 필터링 로직을 추가할 수 있습니다.
      // 예를 들어, 특정 조건을 만족하는 칵테일만 추천으로 보여줄 수 있습니다.
      // return recipes.filter((recipe) => );
      default:
        return recipes;
    }
  };

  return (
    <>
      <p className='recipe_logo'>Cocktail Recipe</p>
      <div className='category'>
        <button className='category__button' onClick={() => setFilter('all')}>
          전체보기
        </button>
        <button className='category__button' onClick={() => setFilter('top10')}>
          Top 10
        </button>
        <button
          className='category__button'
          onClick={() => setFilter('recommend')}
        >
          혼밤이 추천
        </button>
      </div>
      <div className='kategoria'>
        {filterRecipes().map((recipe) => (
          <div key={recipe.cid} className='recipe_card'>
            <img
              src={recipe.imageURL}
              alt={recipe.cname}
              className='recipe_image'
              onClick={() => openModal(recipe)}
            />
            <div className='recipe_details'>
              <h2 onClick={() => openModal(recipe)}>{recipe.cname}</h2>
            </div>
          </div>
        ))}
      </div>

      {modalOpen && selectedRecipe && (
        <div className='modal'>
          <div className='modal_content'>
            <span className='close' onClick={closeModal}>
              &times;
            </span>
            <img
              src={selectedRecipe.imageURL}
              alt={selectedRecipe.cname}
              className='modal_image'
            />
            <div className='modal_details'>
              <h2>{selectedRecipe.cname}</h2>
              <p>{selectedRecipe.description}</p>
              <p>기본 주재료: {selectedRecipe.base}</p>
              <p>도수: {selectedRecipe.degree}%</p>
              <p>
                재료:{' '}
                {Array.isArray(selectedRecipe.material)
                  ? selectedRecipe.material.join(', ')
                  : selectedRecipe.material}
              </p>
              <p>조리 방법: {selectedRecipe.method}</p>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Recipe;
