import React from 'react';
import './Recipe.css';

const recipes = [
  // ... 여러분의 칵테일 정보들 ...
];

const Recipe = () => {
  return (
    <>
      <p className='recipe_logo'>Cocktail Recipe</p>
      <div className='kategoria'>
        {recipes.map((recipe) => (
          <div key={recipe.cid} className='recipe_card'>
            <img
              src={recipe.imageURL}
              alt={recipe.cname}
              className='recipe_image'
            />
            <div className='recipe_details'>
              <h2>{recipe.cname}</h2>
              <p>{recipe.description}</p>
              <p>기본 주재료: {recipe.base}</p>
              <p>도수: {recipe.degree}%</p>
              <p>재료: {recipe.material.join(', ')}</p>
              <p>조리 방법: {recipe.method}</p>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Recipe;
