import React, { useState } from 'react';
import './SearchPage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faMagnifyingGlass,
  faMapLocationDot,
  faPhone,
  faStar,
} from '@fortawesome/free-solid-svg-icons';
import data from '../Test/TemplateData.json';

function SearchPage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [favorites, setFavorites] = useState([]);
  const [favoritedTitles, setFavoritedTitles] = useState([]);

  const toggleFavorite = (id) => {
    const template = data.find((item) => item.id === id);

    if (favorites.includes(id)) {
      setFavorites(favorites.filter((favId) => favId !== id));

      // Check if there's a "Removed" message for the current template
      const removedMessage = `Removed "${template.title}" from favorites!`;
      if (favoritedTitles.includes(removedMessage)) {
        // If there is, remove it from the favoritedTitles state
        setFavoritedTitles(
          favoritedTitles.filter((title) => title !== removedMessage)
        );
      }
    } else {
      setFavorites([...favorites, id]);
      setFavoritedTitles([
        ...favoritedTitles,
        `Added "${template.title}" to favorites!`,
      ]);
    }
  };

  return (
    <div className='search_page'>
      <p className='logo'>HOTPLACE</p>
      <div className='container'>
        <ul className='local_search'>
          <li>지역검색</li>
        </ul>
        <div className='search_bar'>
          <input
            type='text'
            placeholder='Search...'
            onChange={(event) => {
              setSearchTerm(event.target.value);
            }}
          />
          <button type='submit'>
            <FontAwesomeIcon icon={faMagnifyingGlass} className='button' />
          </button>
        </div>
      </div>
      <div className='template_Container'>
        {data
          .filter((val) => {
            if (searchTerm === '') {
              return val;
            } else if (
              val.location.toLowerCase().includes(searchTerm.toLowerCase())
            ) {
              return val;
            }
          })
          .map((val) => {
            const isFavorite = favorites.includes(val.id);

            return (
              <div className='template' key={val.id}>
                <img src={val.image} alt='' />
                <h3>{val.title}</h3>
                <p className='location'>
                  <FontAwesomeIcon icon={faMapLocationDot}></FontAwesomeIcon>
                  {val.location}
                </p>
                <p className='number'>
                  <FontAwesomeIcon icon={faPhone}></FontAwesomeIcon>
                  {val.number}
                </p>
                <button className='hi'>
                  <FontAwesomeIcon
                    icon={faStar}
                    className={`favorite-icon ${isFavorite ? 'active' : ''}`}
                    onClick={() => toggleFavorite(val.id)}
                  />
                </button>
              </div>
            );
          })}
      </div>
      {favoritedTitles.length > 0 && (
        <div className='favorited-titles'>
          {favoritedTitles.map((title, index) => (
            <div
              className='bye'
              key={index}
              onClick={() =>
                setFavoritedTitles(favoritedTitles.filter((t) => t !== title))
              }
            >
              {title}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default SearchPage;
