import React, { useRef, useState } from 'react';
import './Join.css';
import { Grid } from '@mui/material';

export const Join = () => {
  // useREf를 사용해 태그 참조
  const $fileTag = useRef();

  // 이미지파일 상태변수(없으면 안넣어야함)
  const [imgFile, setImgFile] = useState(null);

  // 이미지
  const showThumbnailHandler = (e) => {
    // 첨부된 파일의 정보
    const file = $fileTag.current.files[0]; // e.target.files[0]도 가능

    // 첨부한 파일 이름을 얻은 후 확장자만 추출.(소문자로 일괄 변경)
    const fileExt = file.name.slice(file.name.indexOf('.') + 1).toLowerCase();

    if (
      fileExt !== 'jpg' &&
      fileExt !== 'png' &&
      fileExt !== 'jpeg' &&
      fileExt !== 'gif'
    ) {
      alert('이미지 파일(jpg,png,jpeg,gif)만 등록이 가능합니다!');
      // 이미지가 아닌 파일을 넣어서 막았더라도 , 로그에는 남아있어서 그거까지 지워야함
      // 그렇지 않으면 잘못된 파일을 input 태그가 여전히 가지고 있게 됨. -> 서버 요청시 에러 유발!
      $fileTag.current.value = '';
      return;
    }

    const reader = new FileReader();
    reader.readAsDataURL(file);

    reader.onloadend = () => {
      setImgFile(reader.result);
    };
  };

  const emailHandler = (e) => {
    const inputValue = e.target.value;
    const emailRegex = /^[A-Za-z0-9_\.\-]+@[A-Za-z0-9\-]+\.[A-Za-z0-9\-]+/;

    let msg;
    let flag = false;

    if (!inputValue) {
      msg = '이메일은 필수값 입니다!';
    } else if (!emailRegex.test(inputValue)) {
      msg = '이메일 형식이 올바르지 않습니다.';
    } else {
      // 이메일 중복 체크
      fetchDuplicateCheck(inputValue);
    }

    saveInputState({
      key: 'email',
      inputValue,
      msg,
      flag,
    });
  };

  // 이메일 중복 체크 서버 통신 함수
  const fetchDuplicateCheck = (email) => {
    let msg = '',
      flag = false;
    fetch(`${API_BASE_URL}/check?email=${email}`)
      .then((res) => {
        if (res.status === 200) {
          return res.json();
        }
      })
      .then((json) => {
        // console.log(json);
        if (json) {
          msg = '이메일이 중복되었습니다.';
        } else {
          msg = '사용 가능한 이메일 입니다.';
          flag = true;
        }
        saveInputState({
          key: 'email',
          inputValue: email,
          msg,
          flag,
        });
      })
      .catch((err) => {
        console.log('서버 통신이 원활하지 않습니다.');
      });
  };

  return (
    <div className='join-page'>
      <div className='div'>
        <div className='overlap-group'>
          <div className='group'>
            <div className='div-sign-container'>
              <div className='input'>
                <div className='text-wrapper'>CANCLE</div>
              </div>
              <div className='div-wrapper'>
                <div className='text-wrapper-2'>JOIN</div>
              </div>

              <Grid item xs={12}>
                <div
                  className='thumbnail-box'
                  onClick={() => $fileTag.current.click()}
                >
                  <img
                    src={imgFile || require('../assets/image-beer.png')}
                    alt='profile'
                    width={400}
                    height={190}
                  />
                </div>
                <label className='signup-img-label' htmlFor='profile-img'>
                  CLICK BEER IMAGE!
                </label>
                <input
                  id='profile-img'
                  type='file'
                  style={{ display: 'none' }}
                  accept='image/*'
                  ref={$fileTag}
                  onChange={showThumbnailHandler}
                />
              </Grid>

              <div className='div-placeholder-wrapper'>
                <div className='div-placeholder'>
                  <div className='text-wrapper-3'>
                    <input
                      className='inputText'
                      type='text'
                      placeholder='ENTER YOUR ADDRESS'
                    ></input>
                  </div>
                </div>
              </div>
              <div className='input-2'>
                <div className='div-placeholder'>
                  <div className='text-wrapper-3'>
                    <input
                      className='inputText'
                      type='text'
                      placeholder='ENTER YOUR NICKNAME'
                    ></input>
                  </div>
                </div>
              </div>
              <div className='input-3'>
                <div className='div-placeholder'>
                  <div className='text-wrapper-4'>
                    <input
                      className='inputText'
                      type='password'
                      placeholder='ENTER YOUR PASSWORD(CHECK)'
                    ></input>
                  </div>
                </div>
              </div>
              <div className='input-4'>
                <div className='div-placeholder'>
                  <div className='text-wrapper-4'>
                    <input
                      className='inputText'
                      type='password'
                      placeholder='ENTER YOUR PASSWORD'
                    ></input>
                  </div>
                </div>
              </div>
              <div className='input-5'>
                <div className='div-placeholder'>
                  <div className='text-wrapper-4'>
                    <input
                      className='inputText'
                      type='text'
                      placeholder='ENTER YOUR PHONE NUMBER'
                    ></input>
                  </div>
                </div>
              </div>
              <div className='input-6'>
                <div className='text-wrapper-5'>OVERLAP</div>
              </div>
              <div className='input-7'>
                <div className='text-wrapper-6'>
                  <input
                    className='inputTextEmail'
                    type='text'
                    placeholder='ENTER YOUR EMAIL'
                    onChange={emailHandler}
                  ></input>
                </div>
              </div>
              <div className='join-header'>JOIN</div>
              <div className='heading'>THUMBNAIL IMAGE</div>
            </div>
          </div>
        </div>
        <div className='side-bar'>
          <div className='sidebar-content'>
            <div className='join'>
              <div className='text-wrapper-7'>Join</div>
            </div>
            <div className='login'>
              <div className='text-wrapper-8'>Login</div>
            </div>
            <div className='BOARD'>
              <div className='text-wrapper-9'>BOARD</div>
            </div>
            <div className='CHAT'>
              <div className='text-wrapper-10'>CHAT</div>
            </div>
            <div className='RECIPE'>
              <div className='text-wrapper-11'>RECIPE</div>
            </div>
            <div className='HOTPLACE'>
              <div className='text-wrapper-12'>HOTPLACE</div>
            </div>
          </div>
          <div className='logo'>
            <div className='text-wrapper-13'>HONBAM</div>
          </div>
        </div>
      </div>
    </div>
  );
};
