import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import './Login.css';
import { Link, useNavigate } from 'react-router-dom';
import { API_BASE_URL as BASE, USER } from '../util/host-config';
import AuthContext from '../util/AuthContext';

export const Login = () => {
  const [userId, setUserId] = useState('');
  const [userPw, setUserPw] = useState('');

  const handleLogin = () => {
    if (userId === '') {
      alert('아이디를 입력해주세요.');
    } else if (userPw === '') {
      alert('비밀번호를 입력해주세요.');
    } else {
      axios
        .post('/auth/login', {
          userId: userId,
          userPw: userPw,
        })
        .then(function (obj) {
          localStorage.setItem('refresh_token', obj.data.data.authToken);
          const config = {
            headers: {
              Authorization: `${localStorage.getItem('refresh_token')}`,
            },
          };
          axios
            .get('/try', config)
            .then(function (response) {
              console.log('refresh_token 값 : ' + response.data);
              alert(userId + '님 환영합니다.');
              window.location.href = '/';
            })
            .catch(function (error) {
              console.log('오류 ' + error);
            });
        })
        .catch(() => {
          alert(
            '아이디 또는 비밀번호를 잘못 입력했습니다. 입력하신 내용을 다시 확인해주세요.'
          );
        });
    }
  };
  return (
    <div className='box2'>
      <div className='group'>
        <div className='LOGIN'>
          <div className='overlap-group'>
            <div className='div-sign-container'>
              <form>
                <div className='frame' />
                <div className='div' />
                <div className='input'>
                  <div className='text-wrapper'>
                    <Link to='/Join'>
                      <button type='button' className='JoinButton'>
                        JOIN
                      </button>
                    </Link>
                  </div>
                </div>
                <div className='div-wrapper'>
                  <div className='text-wrapper-2'>
                    <button
                      type='submit'
                      className='LoginButton'
                      onClick={handleLogin}
                    >
                      Login
                    </button>
                  </div>
                </div>
                <div className='div-placeholder-wrapper'>
                  <div className='div-placeholder'>
                    <div className='text-wrapper-3'>
                      <input
                        type='text'
                        label='아이디'
                        margin='normal'
                        value={userId}
                        onChange={(e) => setUserId(e.target.value)}
                        fullWidth
                        variant='standard'
                      ></input>
                    </div>
                  </div>
                </div>
                <div className='input-2'>
                  <div className='div-placeholder'>
                    <div className='text-wrapper-4'>
                      <input
                        type='password'
                        label='패스워드'
                        margin='normal'
                        value={userPw}
                        onChange={(e) => setUserPw(e.target.value)}
                        autoComplete='on'
                        fullWidth
                        variant='standard'
                      ></input>
                    </div>
                  </div>
                </div>
              </form>
              <div className='heading'>LOGIN</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
